import React, { Component } from 'react';
import Wiki from './components/Search';

class App extends Component {

  state = {
    filter : '',
    page : '1'
  }

  apiRequest = () => {
    //"https://rickandmortyapi.com/api/{}" -> sample request
    const filter = this.state.filter;
    const page = this.state.page;
    const url = `https://rickandmortyapi.com/api/${filter}`;

    //console.log(url);

    fetch(url)
      .then(response => response.json())
      .then(result => console.log(result.results.name))
  }

  searchData =  (filter) => {
    this.setState({
      filter
    }, () =>{
      this.apiRequest();
    })
  }

  render () {
    return (
      <div className="App">
        <div className="jumbotron">
          <p className="lead text-center">Rick and Morty Wiki</p>

          <Wiki
            searchData={this.searchData}
          />
        </div>
        {this.state.filter}
      </div>
    );
  }
}

export default App;
