import React from 'react';

const CharInfo = (props) => {

    const {url, image, name, species, status, type, gender, origin, location, episode} = props.character;

    const listItems = episode.map((episode) =>  <li>{episode}</li>);
    const urls = episode.map((episode) =>  {episode});
    // const episodeLis = () => {
    // for (let i = 0; i < episode.length; i++) {
    //     episodeToReturn.push(<li> <a href={episode[i]}></a></li>);
    // }
    // return episodeToReturn;
    // };

    return (
        <div className="row">
            <div className="col-4">
                <div className="card">
                    <img src={image} alt={name} className="card-img-top" />
                    <div className="card-body">
                        <p className="card-text">Name: {name} </p>
                        <p className="card-text">Species: {species} </p>
                        <p className="card-text">Status: {status} </p>
                        <p className="card-text">Type: {type} </p>
                        <p className="card-text">Gender: {gender} </p>
                        <p><a href={origin.url} className="card-text">Origin: {origin.name} </a></p>
                        <p><a href={location.url} className="card-text">Location: {location.name} </a></p>
                        <a href={url} target="_blank" className="btn btn-primary btn-block">Character View </a>
                    </div>
                </div>
            </div>
            <div className="col-8">
                <div className="card">
                    <div className="card-body">
                        {console.log(listItems)}
                        <p className="card-text">Episodes: </p>

                            <p><a href={listItems} target="_blank" className="card-text"> {listItems} </a></p>

                        <ul>
                            <p><a href={urls} className="card-text"> {episode} </a></p>
                            <li><a href={episode[0]} className="card-text"> {episode[0]} </a></li>
                            <li><a href={episode[1]} className="card-text"> {episode[1]} </a></li>

                        </ul>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CharInfo;
